; SHIFT LEFT
; a: number to shift
; b: number of times to shift
shl:
  jz b, [.done]
  push b
  clb
.shift:
  and a, 0x7F
  lda [shltab]
  add16 h, l, a
  lw a
  dec b
  jnz b, [.shift]
  pop b
.done:
  ret

; SHIFT RIGHT
; a: number to shift
; b: number of times to shift
shr:
  jz b, [.done]
  push b
  clb
.shift:
  lda [shrtab]
  add16 h, l, a
  lw a
  dec b
  jnz b, [.shift]
  pop b
.done:
  ret
